import { useState } from 'react';
import React from 'react';
import { useContext } from 'react';
import RoomData from '../context/Room';
import {useNavigate } from 'react-router-dom'
import img from '../utilities/img/bg.jpg';

export default function Home(){
    
    const [username, setUsername] = useState('');
    const [room_id, setRoom_id] = useState();
    const  navigate = useNavigate();

    const {setData} = useContext(RoomData);

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            room_id,
            username
        }

        if(room_id && username){
            setData(data);
            navigate('/chat')
        }
    }

    const handleChangeRoom = (e) => {
        setRoom_id(e.target.value)
    }

    const handleChangeUsername = (e) => {
        setUsername(e.target.value)
    }

    return(
        
    <div className='w-96 h-96 bg-pink-100 relative'>
        <div className='absolute z-0 rounded-full w-36 h-36 bg-gradient-to-r from-[#1845ad] to-[#23a2f6]'></div>
        <div className='absolute z-0 rounded-full w-36 h-36 bg-gradient-to-r from-[#ff512f] to-[#f09819]'></div>
        <div className=" absolute z-10 container flex w-96 h-96 backdrop-blur-sm bg-white/30 justify-center items-center rounded-lg hover:backdrop-blur-lg mt-32 ">
            <div className=" absolute  main-section flex flex-col items-center z-10">
            <label for="username">Username</label>
                <input className="room_id p-3 border-2 mb-10" placeholder="Room_id" onChange={handleChangeRoom} value={room_id} />
                <label >RoomId</label>
                <input className="username p-3 border-2" placeholder="Enter your username" onChange={handleChangeUsername} value={username} />
                
                <button className="Submit text-white border-2 w-20 h-10 border-2 rounded-lg mt-10" onClick={handleSubmit} >Submit</button>
            </div>
        </div>
        </div>
        
    )
}
