import { useState } from "react"
import  useLogin  from "../hooks/useLogin"

const Login = () => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const {Login, error} = useLogin()

  const handleSubmit = async (e) => {
    e.preventDefault()

    await Login(email, password)
  }

  return (
    
    <form className="flex justify-center items-center" onSubmit={handleSubmit}>
      <div className="w-1/2 h-[32rem] bg-white flex flex-col justify-between items-center  p-[2rem]">
      <h3 className="text-black text-5xl font-bold">Log In</h3>

      <div className="flex flex-col justify-between">
      
      <label className="text-black text-xl font-bold">Email address:</label>
      <input 
        type="text" 
        className="w-[20rem] h-[2rem]"
        onChange={(e) => setEmail(e.target.value)} 
        value={email} 
      />

</div>
<div className="flex flex-col justify-between">
      <label className="text-black text-xl font-bold">Password:</label>
      <input 
        type="password" 
        className="w-[20rem] h-[2rem]"
        onChange={(e) => setPassword(e.target.value)} 
        value={password} 
      />

</div>

      <button className="w-[20rem] h-[2rem] bg-[#ff512f]">Log in</button>
      {error && <div className="bg-white">{error}</div>}
      </div>
    </form>
    
  )
}

export default Login
