import React, { useState } from "react";
import useSignup from '../hooks/useSignup'
import {useNavigate } from 'react-router-dom'

function Signup(){

    const [Username, setUsername] = useState('');
    const [password, setpassword] = useState('');
    const {signup,error} = useSignup();

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.prevent.default();
        await signup(Username, password);
        if(error === ''){
           console.log("Fine!!")
        } else{
        }
    }

    return(
        <>
            <form onSubmit={handleSubmit}>
                <label  >Username</label>
                <input placeholder="Username" type="text" name="username" value={Username} onChange={(e) => setUsername(e.target.value)} />
                <label >password</label>
                <input placeholder="Username" type = "password" name="password" value={password} onChange={(e) => setpassword(e.target.value)} />
                <button className="bg-white">Submit</button>
                {error && <div className="text-white">{error}</div>}
            </form>
        </>
    )
}


export default Signup;
