import './App.css';
import PersonToPerson from './pages/p_to_p'
import Home from './pages/home';
import Navbar from './components/navbar';
import { useState } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";


function App() {

  const [data,Setdata] = useState();

  const sendRoomData = (data) => {
    Setdata(data);
  }
  return (
    <div className="App">
      <Navbar data={data} />
      <BrowserRouter>
        <Routes>
          <Route path='' element={<Home sendRoomData={sendRoomData}/>} />
          <Route path='chat' element={<PersonToPerson data={data} />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
