import '../utilities/p_to_p.css';
import {io} from 'socket.io-client';
import {useState, useEffect} from 'react'
import { useContext } from 'react';
import RoomData from '../context/Room';

var socket = io('http://localhost:3001', { transports : ['websocket'] });

export default function PersonToPerson (){

    const [sendMessage, SetsendMessage] = useState('');
    const [receiveMessage, SetreceiveMessage] = useState([]);
    
    const {Data} = useContext(RoomData)

    useEffect(() => {
        if (Data.room_id !== '') {
            socket.emit('join_room', Data.room_id);
        }
    }, [Data]);

    const sendMessages = async () => {

        const messages = {
            room_id: Data.room_id,
            username: Data.username,
            message: sendMessage
        }

        await socket.emit("send_messages", messages);
        SetreceiveMessage(prev => [...prev, sendMessage])
        SetsendMessage("")
    }

    useEffect(() => {
        socket.on("receive_message", (data) => {
            SetreceiveMessage(prevMessages => [...prevMessages, data]);
        });
        return () => {
          socket.off("receive_message");
      };
      }, []);

    return(
        <div className="main_container">
            <h1>{Data.username}</h1>
            <div className="message_container">
                <div className="message_display">
                    {receiveMessage.map((msg, index) => {
                        return(
                            <>
                            <p key={index}>{msg}</p>
                            </>
                        )
                    })}
                </div>
            <div className="message_footer">
                <input className="message_input" value={sendMessage} placeholder="MESSAGE..." onChange={(e) => {SetsendMessage(e.target.value)}} />
                <button className='send_msg' onClick={sendMessages}>Send</button>
            </div>
            </div>
        </div>
    )
}
