import { useState } from 'react';
import React from 'react';
import { useContext } from 'react';
import RoomData from '../context/Room';
import {useNavigate } from 'react-router-dom'

export default function Home(){
    
    const [username, setUsername] = useState('');
    const [room_id, setRoom_id] = useState();
    const  navigate = useNavigate();

    const {setData} = useContext(RoomData);

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            room_id,
            username
        }

        if(room_id && username){
            setData(data);
            navigate('/chat')
        } else{
            return <h1>ALL Feild should be filled</h1>
        }

        setData(data);

    }

    const handleChangeRoom = (e) => {
        setRoom_id(e.target.value)
    }

    const handleChangeUsername = (e) => {
        setUsername(e.target.value)
    }

    return(
        <div className="container">
            <div className="main-section">
                <input className="room_id" placeholder="Room_id" onChange={handleChangeRoom} value={room_id} />
                <input className="username" placeholder="Enter your username" onChange={handleChangeUsername} value={username} />
                <button className="Submit" onClick={handleSubmit} >Submit</button>
            </div>
        </div>
    )
}
