import { useState } from 'react';
import React from 'react';
import { useContext } from 'react';
import RoomData from '../context/Room';
import {useNavigate } from 'react-router-dom'

export default function Home(){
    
    const [username, setUsername] = useState('');
    const [room_id, setRoom_id] = useState();
    const  navigate = useNavigate();

    const {setData} = useContext(RoomData);

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            room_id,
            username
        }

        if(room_id && username){
            setData(data);
            navigate('/chat')
        }
    }

    const handleChangeRoom = (e) => {
        setRoom_id(e.target.value)
    }

    const handleChangeUsername = (e) => {
        setUsername(e.target.value)
    }

    return(
        <div className="container flex w-96 h-96 bg-black opacity-75 justify-center items-center border-2 rounded-lg">
            <div className="main-section flex flex-col items-center">
                <input className="room_id p-3 border-2 mb-10" placeholder="Room_id" onChange={handleChangeRoom} value={room_id} />
                <input className="username p-3 border-2" placeholder="Enter your username" onChange={handleChangeUsername} value={username} />
                <button className="Submit text-white border-2 w-20 h-10 border-2 rounded-lg" onClick={handleSubmit} >Submit</button>
            </div>
        </div>
    )
}
