import { useState } from "react"
import  useLogin  from "../hooks/useLogin"

const Login = () => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const {Login, error} = useLogin()

  const handleSubmit = async (e) => {
    e.preventDefault()

    await Login(email, password)
  }

  return (
    <form className="w-[30rem] h-[70rem] bg-white flex flex-column justify-center items-center" onSubmit={handleSubmit}>
      <h3 className="text-white">Log In</h3>
      
      <label>Email address:</label>
      <input 
        type="text" 
        className="bg-white"
        onChange={(e) => setEmail(e.target.value)} 
        value={email} 
      />
      <label>Password:</label>
      <input 
        type="password" 
        onChange={(e) => setPassword(e.target.value)} 
        value={password} 
      />

      <button className="bg-white">Log in</button>
      {error && <div className="bg-white">{error}</div>}
    </form>
  )
}

export default Login
