import '../utilities/p_to_p.css';
import {io} from 'socket.io-client';
import {useState} from 'react'

var socket = io('http://localhost:3001', { transports : ['websocket'] });

export default function PersonToPerson (props){

    const [sendMessage, SetsendMessage] = useState('');
    const [receiveMessage, SetreceiveMessage] = useState([]);
    const data = props.data

    if(data.room_id !== ''){
        socket.emit('join_room', data.room_id);
    }

    const sendMessages = async () => {
        await socket.emit("send_messages", sendMessage);
        SetreceiveMessage(prev => [...receiveMessage, prev])
    }

    return(
        <div className="main_container">
            <div className="message_container">
                <div className="message_display">
                    <h2>All messgaes goes here</h2>
                </div>
            <div className="message_footer">
                <input className="message_input" placeholder="MESSAGE..." onChange={(e) => {SetsendMessage(e.target.value)}} />
            </div>
            </div>
        </div>
    )
}
