import { useState } from 'react';
import React from 'react';
import { useContext } from 'react';
import RoomData from '../context/Room';
import {useNavigate } from 'react-router-dom'
import img from '../utilities/img/bg.jpg';

export default function Home(){
    
    const [username, setUsername] = useState('');
    const [room_id, setRoom_id] = useState();
    const  navigate = useNavigate();

    const {setData} = useContext(RoomData);

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            room_id,
            username
        }

        if(room_id && username){
            setData(data);
            navigate('/chat')
        }
    }

    const handleChangeRoom = (e) => {
        setRoom_id(e.target.value)
    }

    const handleChangeUsername = (e) => {
        setUsername(e.target.value)
    }

    return(
        
        <div>
        <div className="relative w-80 h-96 mx-auto mt-24">
            <div className="absolute inset-0 rounded-full bg-gradient-to-br from-blue-600 to-blue-400"></div>
            <div className="absolute bottom-0 right-0 w-40 h-40 rounded-full bg-gradient-to-br from-red-600 to-yellow-600"></div>
        </div>
        <form className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 bg-white bg-opacity-10 backdrop-blur-md border border-white border-opacity-10 rounded-lg shadow-xl p-10">
            <h3 className="text-white text-center text-2xl font-semibold">Login Here</h3>

            <label htmlFor="username" className="block mt-6 text-white font-semibold">Username</label>
            <input type="text" id="username" placeholder="Email or Phone" className="block w-full py-3 px-4 mt-1 bg-white bg-opacity-20 rounded-md placeholder-gray-400 focus:outline-none focus:ring focus:ring-blue-400"/>

            <label htmlFor="password" className="block mt-4 text-white font-semibold">Password</label>
            <input type="password" id="password" placeholder="Password" className="block w-full py-3 px-4 mt-1 bg-white bg-opacity-20 rounded-md placeholder-gray-400 focus:outline-none focus:ring focus:ring-blue-400"/>

            <button className="mt-8 w-full bg-white text-black py-4 px-6 text-xl font-semibold rounded-md cursor-pointer">Log In</button>
            <div className="flex mt-6">
                <div className="w-36 h-16 bg-white bg-opacity-20 rounded-md py-2 px-4 mr-6 flex items-center justify-center cursor-pointer transition duration-300 hover:bg-opacity-30">
                    <i className="fab fa-google text-xl mr-2"></i>
                    Google
                </div>
                <div className="w-36 h-16 bg-white bg-opacity-20 rounded-md py-2 px-4 flex items-center justify-center cursor-pointer transition duration-300 hover:bg-opacity-30">
                    <i className="fab fa-facebook text-xl mr-2"></i>
                    Facebook
                </div>
            </div>
        </form>
    </div>
        
    )
}
