import { io } from 'socket.io-client';
import { useState, useEffect, useContext } from 'react';
import RoomData from '../context/Room';
import { useNavigate } from 'react-router-dom';

const socket = io('http://localhost:3001', { transports: ['websocket'] });

export default function PersonToPerson() {
    const [sendMessage, SetsendMessage] = useState('');
    const [receiveMessage, SetreceiveMessage] = useState([]);

    const navigate = useNavigate();
    const { Data } = useContext(RoomData);

    useEffect(() => {
        if (Data.room_id !== '') {
            socket.emit('join_room', Data.room_id);
        }
    }, [Data]);

    const sendMessages = async () => {
        const messageObject = {
            room_id: Data.room_id,
            username: Data.username,
            message: sendMessage
        };

        await socket.emit("send_messages", messageObject);
        SetreceiveMessage(prev => [...prev, messageObject]);
        SetsendMessage("");
    };

    useEffect(() => {
        socket.on("receive_message", (data) => {
            SetreceiveMessage(prevMessages => [...prevMessages, data]);
        });

        return () => {
            socket.off("receive_message");
        };
    }, []);

    const existChat = () => {
        localStorage.removeItem('roomData');
        navigate('/');
        window.location.reload(false);
    };

    return (
        <div className="main_container w-screen h-screen flex justify-center flex-col items-center">
            <div className="message_container w-[21rem] h-[28rem] border-2 rounded-ld border-white flex flex-col justify-between overflow-hidden">
                <div className="message_display">
                    {receiveMessage.map((msg, index) => (
                        <div key={index} className='w-[9rem] h-[1.5rem] bg-blue-500 ml-2 mt-2 rounded-lg'>
                            <p className='ml-2'>{msg.message}</p>
                        </div>
                    ))}
                </div>
                <div className="message_footer w-full">
                    <input
                        className="message_input w-full"
                        value={sendMessage}
                        placeholder="MESSAGE..."
                        onChange={(e) => SetsendMessage(e.target.value)}
                    />
                    <button className='send_msg bg-blue-500' onClick={sendMessages}>Send</button>
                </div>
            </div>
            <button className='w-[5rem] h-[2rem] rounded-lg bg-white' onClick={existChat}>Leave Room</button>
        </div>
    );
}
